mapper.insert:
		insert into tb_gallery
		(
    		gallery_num
    		,cat_num
    		,title
    		,author
    		,publisher
    		,creator
		) values
		(
			TB_GALLERY_SEQ.nextval
			,#{catNum}
			,#{title}
			,#{author}
			,#{publisher}
			,#{creator}
		)

    		SELECT TB_GALLERY_SEQ.currval FROM dual
